import React, { useState } from 'react';
import './App.css';

const dummyContent = {
  headlines: [
    "Transform Your Ideas into Reality",
    "Innovate with Confidence",
    "Create Something Amazing",
    "Design Your Future",
    "Build Better Experiences"
  ],
  subheadlines: [
    "Discover the power of innovation and creativity in every project",
    "Join us on a journey to create something extraordinary",
    "Experience the future of design and development",
    "Unleash your potential with our cutting-edge solutions",
    "Make your mark in the digital world"
  ],
  images: [
    "https://images.unsplash.com/photo-1551434678-e076c223a692?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
    "https://images.unsplash.com/photo-1498050108023-c5249f4df085?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
    "https://images.unsplash.com/photo-1460925895917-afdab827c52f?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
    "https://images.unsplash.com/photo-1516321318423-f06f85e504b3?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
    "https://images.unsplash.com/photo-1551434678-e076c223a692?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80"
  ]
};

function App() {
  const [content, setContent] = useState({
    headline: "Transform Your Ideas into Reality",
    subheadline: "Discover the power of innovation and creativity in every project",
    image: "https://images.unsplash.com/photo-1551434678-e076c223a692?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80"
  });

  const [isEditing, setIsEditing] = useState({
    headline: false,
    subheadline: false
  });

  const regenerateContent = () => {
    const randomIndex = Math.floor(Math.random() * dummyContent.headlines.length);
    setContent({
      headline: dummyContent.headlines[randomIndex],
      subheadline: dummyContent.subheadlines[randomIndex],
      image: dummyContent.images[randomIndex]
    });
  };

  const handleTextChange = (field, value) => {
    setContent(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleKeyDown = (e, field) => {
    if (e.key === 'Enter') {
      setIsEditing(prev => ({
        ...prev,
        [field]: false
      }));
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 items-center">
          <div className="space-y-6">
            <div
              className="editable-text text-4xl md:text-5xl font-bold text-gray-900"
              onClick={() => setIsEditing(prev => ({ ...prev, headline: true }))}
            >
              {isEditing.headline ? (
                <input
                  type="text"
                  value={content.headline}
                  onChange={(e) => handleTextChange('headline', e.target.value)}
                  onBlur={() => setIsEditing(prev => ({ ...prev, headline: false }))}
                  onKeyDown={(e) => handleKeyDown(e, 'headline')}
                  className="w-full bg-transparent border-none focus:outline-none"
                  autoFocus
                />
              ) : (
                content.headline
              )}
            </div>
            
            <div
              className="editable-text text-xl text-gray-600"
              onClick={() => setIsEditing(prev => ({ ...prev, subheadline: true }))}
            >
              {isEditing.subheadline ? (
                <input
                  type="text"
                  value={content.subheadline}
                  onChange={(e) => handleTextChange('subheadline', e.target.value)}
                  onBlur={() => setIsEditing(prev => ({ ...prev, subheadline: false }))}
                  onKeyDown={(e) => handleKeyDown(e, 'subheadline')}
                  className="w-full bg-transparent border-none focus:outline-none"
                  autoFocus
                />
              ) : (
                content.subheadline
              )}
            </div>

            <div className="flex space-x-4">
              <button className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-colors">
                Get Started
              </button>
              <button
                onClick={regenerateContent}
                className="bg-gray-200 text-gray-700 px-6 py-3 rounded-lg hover:bg-gray-300 transition-colors"
              >
                Regenerate with AI
              </button>
            </div>
          </div>

          <div className="relative">
            <img
              src={content.image}
              alt="Hero illustration"
              className="rounded-lg shadow-xl w-full h-auto"
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
